    private void btnSumActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSumActionPerformed
btnSum.addActionListener(this);
if (evt.getSource()==btnSum){
            cad1= txtCalculo1.getText();
            txtCalculo1.setText(null);
            operador= 1;
            
        }
    }//GEN-LAST:event_btnSumActionPerformed

    private void btnIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIgualActionPerformed
if (evt.getSource()==btnIgual){
            cad2= txtCalculo1.getText();
                        
            if (operador==1){
                x= Integer.parseInt(cad1);
                y= Integer.parseInt(cad2);
                double suma= x+y;
                String resultado= String.valueOf(suma);
                txtCalculo1.setText(null);
                txtCalculo1.setText(resultado);
 
            }
            if (operador==2){
                x= Integer.parseInt(cad1);
                y= Integer.parseInt(cad2);
                double resta= x-y;
                String resultado= String.valueOf(resta);
                txtCalculo1.setText(null);
                txtCalculo1.setText(resultado);
 
            }
            if (operador==3){
                x= Integer.parseInt(cad1);
                y= Integer.parseInt(cad2);
                double producto= x*y;
                String resultado= String.valueOf(producto);
                txtCalculo1.setText(null);
                txtCalculo1.setText(resultado);
 
            }
            if (operador==4){
                x= Integer.parseInt(cad1);
                y= Integer.parseInt(cad2);
                if (x>y){
                    double division= x/y;
                    String resultado= String.valueOf(division);
                    txtCalculo1.setText(null);
                    txtCalculo1.setText(resultado);
                } else {
                    setTitle("La operaciÃ³n no puede realizarse.");
                }
                
 
            }
            operador=0;
        }
    }//GEN-LAST:event_btnIgualActionPerformed

