    public void actionPerformed (ActionEvent evt){
        char c = ((JButton) evt.getSource()).getText().charAt(0); 
        
        
        if (c >= '0' && c <= '9') {
            txtCalculo1.setText(txtCalculo1.getText() + c);
        }
        
        

        if (evt.getSource()==btnRes){
            cad1= txtCalculo1.getText();
            txtCalculo1.setText(null);
            operador= 2;
            
        }
        if (evt.getSource()==btnMult){
            cad1= txtCalculo1.getText();
            txtCalculo1.setText(null);
            operador= 3;
            
        }
        if (evt.getSource()==btnDiv){
            cad1= txtCalculo1.getText();
            txtCalculo1.setText(null);
            operador= 4;
            
        }
        
        if (evt.getSource()==btnBorrar){
            txtCalculo1.setText(null);
            cad1=null;
            cad2=null;
            x=0;
            y=0;
            operador=0;
            
        }
        
        
        
                                                
    }   

